void moveFor(int time)
{
	 clearTimer(T1);

	 while(time1[T1]<time){
	 motor[Rmotor] = 127;
   motor[Rmotor] = 127;

   motor[Lmotor] = 127;
   motor[Lmotor] = 127;}

}
void drivR(int time){
	while(time1[T1]<time){
	 motor[Rmotor] = -20;
   motor[Rmotor] = -20;

   motor[Lmotor] = 127;
   motor[Lmotor] = 127;}
}

void autR()
{
	  moveFor(1000);

	  drivR(2000);
	  moveFor(1501);



}

task main()
{

short armMode = -1;
int armNum = 0;

while(true) {
	
  if(armMode == -1){
   // right side = Channel 2 joystick value
   motor[Rmotor] = vexRT[Ch2];
   motor[Rmotor] = vexRT[Ch2];

   // left side = Channel 3 joystick value
   motor[Lmotor] = vexRT[Ch3];
   motor[Lmotor] = vexRT[Ch3];
}
if(armMode == 1){
   motor[Pincer] = 	vexRT[Ch4];
   motor[Pulley] = vexRT[Ch2];

}


   

   if(vexRT(Btn7D) == 1){
     autR();

     }
     
//to change to arm mode
if(vexRT(Btn8U) == 1){
  armMode *= -1;		
}
 wait1Msec(250);  
     
}



}



